import { initializeApp } from 'firebase/app';
import { getAnalytics } from "firebase/analytics";
import { getDatabase, ref, onValue, get, child} from "firebase/database";
import React, {useEffect, useState} from 'react';


export const SearchView = (props) => {
  // create a variable for the data + a setter
  const [db_data, set_db_data] = useState(() => [])

  // firebase config
  const firebaseConfig = {
    apiKey: "AIzaSyAQ6USPFRL3Uqps1aqErhcNyRlO2XblHj0",
    authDomain: "interview-50399.firebaseapp.com",
    projectId: "interview-50399",
    storageBucket: "interview-50399.appspot.com",
    messagingSenderId: "503019432099",
    appId: "1:503019432099:web:9394f58478b734996db716",
    measurementId: "G-C8XFN5VGFS"
  };
  const app = initializeApp(firebaseConfig);
  const database = getDatabase();

  // get the data before the app loads
  useEffect(() => {
    const dbRef = ref(getDatabase());
    get(child(dbRef, '/')).then((snapshot) => {
      const data = snapshot.val()
      const list = []
      for (let id in data){
        list.push(data[id])
      }
      // console.log(data)
      set_db_data(data)
      let options = []
      for (let i = 0; i < db_data.length; i++){
        options.push(<option key={db_data[i]['First']} value={db_data[i]['First']}>{db_data[i]}</option>)
      }
    }).catch((error) => {
      console.error(error);
    });
  }, [])
  console.log(db_data)

  // create options out of the data
  // render
  return (
    <div>
      <input list="users" name="userSearch"/>
      <datalist id="users">
        {options}
      </datalist>
    </div>
  )
}
